describe("Calc", function() {

  beforeEach(function() {
    $input.value = 0
  });

  it("input init should be 0", function() {
    expect(+$input.value).toEqual(0);
  });
  it("if user clicked on the reset button, result should be 0", function() {
    $input.value = 10;
    _reset();
    expect(+$input.value).toEqual(0);
  });
  it("is number", function() {
    expect(_isNumber('1')).toEqual(true);
    expect(_isNumber('2')).toEqual(true);
    expect(_isNumber('3')).toEqual(true);
    expect(_isNumber('4')).toEqual(true);
    expect(_isNumber('5')).toEqual(true);
    expect(_isNumber('6')).toEqual(true);
    expect(_isNumber('7')).toEqual(true);
    expect(_isNumber('8')).toEqual(true);
    expect(_isNumber('9')).toEqual(true);
    expect(_isNumber('0')).toEqual(true);
    expect(_isNumber('÷')).toEqual(false);
    expect(_isNumber('.')).toEqual(false);
    expect(_isNumber('=')).toEqual(false);
    expect(_isNumber('C')).toEqual(false);
    expect(_isNumber('+')).toEqual(false);
    expect(_isNumber('-')).toEqual(false);
    expect(_isNumber('x1')).toEqual(false);
  });
  it("is dot", function() {
    expect(_isDot('1')).toEqual(false);
    expect(_isDot('.')).toEqual(true);
  });
  it("is operator", function() {
    expect(_isOperator('1')).toEqual(false);
    expect(_isOperator('+')).toEqual(true);
    expect(_isOperator('-')).toEqual(true);
    expect(_isOperator('x')).toEqual(true);
    expect(_isOperator('÷')).toEqual(true);
  });
  it("is equally", function() {
    expect(_isEqually('1')).toEqual(false);
    expect(_isEqually('=')).toEqual(true);
  });
  it("get last symbol", function() {
    $input.value = 11;
    expect(_getLastSymbol()).toEqual('1');
    $input.value = 12345;
    expect(_getLastSymbol()).toEqual('5');
  });
  it("replace operator", function() {
    expect(_replacesOperator('2x2')).toEqual('2*2');
    expect(_replacesOperator('8÷2')).toEqual('8/2');
    expect(_replacesOperator('8+2-1')).toEqual('8+2-1');
  });
  it("is remove", function() {
    expect(_isRemove('←')).toEqual(true);
    expect(_isRemove('=')).toEqual(false);
  });
  it("remove symbol", function() {
    $input.value = 123;
    _removeSymbol();
    expect($input.value).toEqual('12');
    $input.value = 1;
    _removeSymbol();
    expect($input.value).toEqual('0');
  });
  it("get equal", function() {
    $input.value = '1+1';
    _getEquall();
    expect($input.value).toEqual('2');
    $input.value = '100+100-100x5';
    _getEquall();
    expect($input.value).toEqual('-300');
    $input.value = '5x5+100-50';
    _getEquall();
    expect($input.value).toEqual('75');
    $input.value = '2.5+2.1';
    _getEquall();
    expect($input.value).toEqual('4.6');
    $input.value = '55.5+0.2';
    _getEquall();
    expect($input.value).toEqual('55.7');
  });
});
